import React, { useState, useEffect } from "react";
import { StyleSheet, View } from "react-native";
import { SCREENS } from "./constants";

import Home from "./screens/Home";
import Feed from "./screens/Feed";
import Footer from "./components/common/Footer";
import Header from "./components/common/Header";

export default function App() {
  const [content, setContent] = useState(SCREENS.home);
  let prevScreen = SCREENS.feed;

  const goTo = (screen) => {
    prevScreen = content;
    setContent(screen);
  };

  const goBack = () => {
    let prevScreenTmp = prevScreen;
    prevScreen = content;
    setContent(prevScreenTmp);
  };

  let view = <Home goTo={goTo} prevScreen={SCREENS.feed} />;
  switch (content) {
    case SCREENS.feed:
      view = <Feed goTo={goTo} prevScreen={SCREENS.home} />;
      break;
    case SCREENS.detail:
      break;
  }

  return (
    <View style={styles.container}>
      <Header goBack={goBack} />
      {view}
      <Footer goTo={goTo} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: "#fff",
  },
});
